import React from "react";

import "./Chart.css";
import ChartBar from './ChartBar';

const Chart = (props) => {
    //mapping props obj array into numbers
    let dataPointValues = props.dataPoints.map(dataPoint => dataPoint.value);
    let valueMax = Math.max(...dataPointValues)

    // takes props and maps it into jsx component
    return ( 
    <div className="Chart">
    {props.dataPoints.map(dataPoint => ( 
    <ChartBar 
        value={dataPoint.value}
        maxValue={valueMax} 
        metric={dataPoint.metric}
        id={dataPoint.metric} 
        />
    ))}
    </div>
    );
};

export default Chart;
