import React from "react";

import "./Chart.css";
import ChartBar from './ChartBar';

const Chart = (props) => {
    //mapping props obj array into numbers
    let dataPointValues = props.spendingData.map(dataPoint => spendingData.value);
    let valueMax = Math.max(...dataPointValues)

    // takes props and maps it into jsx component
    return <div className="Chart">
    {props.spendingData.map(finData => <ChartBar 
    value={finData.value}
    maxValue={valueMax} 
    metric={finData.metric}
    id={finData.metric} />)}
    </div>
};

export default Chart;
